'use strict';

/*
* When adding names to the namespace, ensure that they are added in alphabetical order according to alias (namespace key).
*/

var ns = [];

ns.push({
	'alias': 'waterfall',
	'path': '@stdlib/utils/series-waterfall',
	'value': require( '@stdlib/utils/series-waterfall' ),
	'type': 'Function',
	'related': []
});

ns.push({
	'alias': 'whileAsync',
	'path': '@stdlib/utils/while-async',
	'value': require( '@stdlib/utils/while-async' ),
	'type': 'Function',
	'related': [
		'@stdlib/utils/do-until-async',
		'@stdlib/utils/do-while-async',
		'@stdlib/utils/until-async',
		'@stdlib/utils/while',
		'@stdlib/utils/while-each-async'
	]
});

ns.push({
	'alias': 'whileEach',
	'path': '@stdlib/utils/while-each',
	'value': require( '@stdlib/utils/while-each' ),
	'type': 'Function',
	'related': [
		'@stdlib/utils/until-each',
		'@stdlib/utils/while-each-async',
		'@stdlib/utils/while-each-right'
	]
});

ns.push({
	'alias': 'whileEachRight',
	'path': '@stdlib/utils/while-each-right',
	'value': require( '@stdlib/utils/while-each-right' ),
	'type': 'Function',
	'related': [
		'@stdlib/utils/while-each',
		'@stdlib/utils/until-each-right'
	]
});

ns.push({
	'alias': 'whilst',
	'path': '@stdlib/utils/while',
	'value': require( '@stdlib/utils/while' ),
	'type': 'Function',
	'related': [
		'@stdlib/utils/do-until',
		'@stdlib/utils/do-while',
		'@stdlib/utils/until',
		'@stdlib/utils/while-async',
		'@stdlib/utils/while-each'
	]
});

ns.push({
	'alias': 'writeFile',
	'path': '@stdlib/fs/write-file',
	'value': require( '@stdlib/fs/write-file' ),
	'type': 'Function',
	'related': [
		'@stdlib/fs/exists',
		'@stdlib/fs/read-file'
	]
});


// EXPORTS //

module.exports = ns;
