'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetical order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// MAIN //

/**
* Top-level namespace.
*
* @namespace streams
*/
var streams = {};

/**
* @name stderr
* @memberof streams
* @readonly
* @type {WritableStream}
* @see {@link module:@stdlib/streams/base/stderr}
*/
setReadOnly( streams, 'stderr', require( '@stdlib/streams/base/stderr' ) );

/**
* @name stdin
* @memberof streams
* @readonly
* @type {ReadableStream}
* @see {@link module:@stdlib/streams/base/stdin}
*/
setReadOnly( streams, 'stdin', require( '@stdlib/streams/base/stdin' ) );

/**
* @name stdout
* @memberof streams
* @readonly
* @type {WritableStream}
* @see {@link module:@stdlib/streams/base/stdout}
*/
setReadOnly( streams, 'stdout', require( '@stdlib/streams/base/stdout' ) );


// EXPORTS //

module.exports = streams;
