'use strict';

/*
* When adding modules to the namespace, ensure that they are added in alphabetical order according to module name.
*/

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );


// MAIN //

/**
* Top-level namespace.
*
* @namespace streams
*/
var streams = {};

/**
* @name debugStream
* @memberof streams
* @readonly
* @type {TransformStream}
* @see {@link module:@stdlib/streams/utils/debug}
*/
setReadOnly( streams, 'debugStream', require( '@stdlib/streams/utils/debug' ) );

/**
* @name inspectStream
* @memberof streams
* @readonly
* @type {TransformStream}
* @see {@link module:@stdlib/streams/utils/inspect}
*/
setReadOnly( streams, 'inspectStream', require( '@stdlib/streams/utils/inspect' ) );

/**
* @name joinStream
* @memberof streams
* @readonly
* @type {TransformStream}
* @see {@link module:@stdlib/streams/utils/join}
*/
setReadOnly( streams, 'joinStream', require( '@stdlib/streams/utils/join' ) );

/**
* @name splitStream
* @memberof streams
* @readonly
* @type {TransformStream}
* @see {@link module:@stdlib/streams/utils/split}
*/
setReadOnly( streams, 'splitStream', require( '@stdlib/streams/utils/split' ) );

/**
* @name transformStream
* @memberof streams
* @readonly
* @type {TransformStream}
* @see {@link module:@stdlib/streams/utils/transform}
*/
setReadOnly( streams, 'transformStream', require( '@stdlib/streams/utils/transform' ) );


// EXPORTS //

module.exports = streams;
